<?class TestCoverageTestCase extends TestCase{	protected $coverage;	public function __construct()	{			$this->coverage = new TestCoverage();			}			public function addThenRemoveCovergeCalls_simpleScript_keepsCodeUntouched()	{			$code = '<? myfunc("hello worlds"); ?>';				$coveredCode = $this->coverage->addCoverageCalls( $code );				$clearCode = $this->coverage->removeCoverageCalls( $coveredCode );				$this->assertEqual( $code , $clearCode );		}		public function addThenRemoveCovergeCalls_simpleScript2_keepsCodeUntouched() 	{				$code = file_get_contents( dirname(__FILE__) .'/samples/sample1.php' );						$coveredCode = $this->coverage->addCoverageCalls( $code );				$clearCode = $this->coverage->removeCoverageCalls( $coveredCode );				$this->assertEqual( $code , $clearCode );		}	}?>