<?phpclass ArticleModelMySQLDataDriver extends MySQLDataDriver{  public function getArticlesWithIDInRange( $lo, $hi )  {    return      $this->qdp        ->prepare('select * from article where id between ? and ?' , 'ii')        ->executeWith( $lo , $hi )        ->toArray()      ;  }  public function getArticlesWithOddIds()  {    return      $this->qdp        ->execute('select * from article where id%2 !=0')        ->toArray()      ;  }}