<?phpclass EntityVerboser extends EntityCrawler{  protected function handleEntity( $sourceEntry , $entityName )  {    $entity = new $entityName();    $fields = $entity->getFields();    $sc = ShellColors::getInstance();    $fields = array_map(function( $x ){ return ShellColors::getInstance()->getColoredString($x,"yellow"); } , $fields);    $fieldList = "\t" . implode("{$suffix}\n\t" , $fields) . "{$suffix}";    echo  $sc->getColoredString($entityName ,"green") . "\n{ {$suffix} \n"      . $fieldList . "\n} {$suffix}\n\n" . $sc->getColoredString("","white");  }  public function verbose( $sourceEntry )  {    $this->resolveProject( $sourceEntry );    $this->traverse( $sourceEntry );  }}
